from django.urls import reverse
from rest_framework import status
from rest_framework.test import APITestCase, APIClient
from user.models import User
from .models import Post


class PostTests(APITestCase):

    def setUp(self):
        self.client = APIClient()
        self.access_token = None
        self.posts_id = None

        user = User.objects.create_superuser(
            last_name="Lastname",
            first_name="Firstname",
            email="exemple@exemple.com",
            username="exemple@exemple.com",
            password='password1234',
            token='foo',
            is_superuser=True
        )

        regular_user = User.objects.create_user(
            last_name="Lastname",
            first_name="Firstname",
            email="regular@exemple.com",
            username="regular@exemple.com",
            password='password1234',
            token='foo',
        )

    def test_create_posts_valid_value(self):

        url = '/token'
        data = {'username': 'exemple@exemple.com', 'password': 'password1234'}
        response = self.client.post(url, data, format='json')
        data = response.json()
        
        self.client.credentials(HTTP_AUTHORIZATION='Bearer ' + data['access'])
        
        data = {
            "title": "Title",
            "image_url": "image url",
            "content": "Post content",
        }

        url = '/posts/'
        response = self.client.post(url, data, format='json')
        self.assertEqual(response.status_code, status.HTTP_201_CREATED)
    
    def test_create_posts_unlogin_user(self):

        data = {
            "title": "Title",
            "image_url": "image url",
            "content": "Post content",
        }

        url = '/posts/'
        response = self.client.post(url, data, format='json')
        self.assertEqual(response.status_code, status.HTTP_401_UNAUTHORIZED)
          
    def test_create_posts_missing_value(self):

        url = '/token'
        data = {'username': 'exemple@exemple.com', 'password': 'password1234'}
        response = self.client.post(url, data, format='json')
        data = response.json()
        
        self.client.credentials(HTTP_AUTHORIZATION='Bearer ' + data['access'])
        

        data = {
            
            "title": "",
            "image_url": "",
            "content": "",
        }
        
        url = '/posts/'
        response = self.client.post(url, data, format='json')
        data = response.json()
        
        self.assertTrue(True, data['errors']['title'])
        self.assertTrue(True, data['errors']['image_url'])
        self.assertTrue(True, data['errors']['content'])
        
        self.assertEqual(response.status_code, status.HTTP_404_NOT_FOUND)
    
