    def test_update_posts_valid_value(self):
        
        url = '/token'
        data = {'username': 'exemple@exemple.com', 'password': 'password1234'}
        response = self.client.post(url, data, format='json')
        data = response.json()
        
        self.client.credentials(HTTP_AUTHORIZATION='Bearer ' + data['access'])
        
        # Create posts
        data = {
            "title": "Title",
            "image_url": "image url",
            "content": "Post content",
        }

        
        url = '/posts/'
        response = self.client.post(url, data, format='json')
        
        # Read all posts and get create posts id
        url = '/posts/all'
        response = self.client.get(url, format='json')
        data = response.json()
        self.posts_id = data[0]["public_id"]
        
        
        # Update posts
        data = {
            "title": "New Title",
            "image_url": "image url",
            "content": "New post content",
        }

        
        url = '/posts/' + self.posts_id
        response = self.client.put(url, data, format='json')
        self.assertEqual(response.status_code, status.HTTP_200_OK)

